@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(222, 84%, 4.9%);
  --foreground: hsl(210, 40%, 98%);
  --muted: hsl(217, 33%, 17%);
  --muted-foreground: hsl(215, 20.2%, 65.1%);
  --popover: hsl(222, 84%, 4.9%);
  --popover-foreground: hsl(210, 40%, 98%);
  --card: hsl(222, 84%, 4.9%);
  --card-foreground: hsl(210, 40%, 98%);
  --border: hsl(217, 33%, 17%);
  --input: hsl(217, 33%, 17%);
  --primary: hsl(224, 71.4%, 56.1%);
  --primary-foreground: hsl(210, 20%, 98%);
  --secondary: hsl(217, 33%, 17%);
  --secondary-foreground: hsl(210, 40%, 98%);
  --accent: hsl(217, 33%, 17%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --ring: hsl(224, 71.4%, 56.1%);
  --radius: 0.75rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', system-ui, sans-serif;
  }
}

@layer components {
  .glass-effect {
    background: rgba(15, 23, 42, 0.95);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  .glass-card {
    background: rgba(15, 23, 42, 0.4);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.08);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
    transition: all 0.3s ease;
  }

  .glass-card:hover {
    background: rgba(15, 23, 42, 0.6);
    border: 1px solid rgba(255, 255, 255, 0.12);
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.4);
  }

  .gradient-text {
    background: linear-gradient(135deg, hsl(224, 71.4%, 56.1%), hsl(262, 83%, 70%), hsl(189, 94%, 57%));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .card-hover {
    transition: all 0.3s ease;
  }

  .card-hover:hover {
    transform: translateY(-8px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .animate-slide-up {
    animation: slideUp 0.5s ease-out;
  }

  .animate-fade-in {
    animation: fadeIn 0.8s ease-out;
  }

  .animate-bounce-in {
    animation: bounceIn 0.6s ease-out;
  }

  .animate-scale-in {
    animation: scaleIn 0.3s ease-out;
  }

  /* Custom Scrollbar */
  .custom-scrollbar {
    scrollbar-width: thin;
    scrollbar-color: rgba(255, 255, 255, 0.3) transparent;
  }

  .custom-scrollbar::-webkit-scrollbar {
    width: 6px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    background: transparent;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    background: rgba(255, 255, 255, 0.3);
    border-radius: 3px;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: rgba(255, 255, 255, 0.5);
  }

  /* Loading Shimmer */
  .loading-shimmer {
    background: linear-gradient(90deg, 
      rgba(255,255,255,0.1) 25%, 
      rgba(255,255,255,0.2) 50%, 
      rgba(255,255,255,0.1) 75%
    );
    background-size: 200% 100%;
    animation: shimmer 2s infinite;
  }

  /* Enhanced Glass Cards */
  .glass-card-enhanced {
    background: rgba(15, 23, 42, 0.5);
    backdrop-filter: blur(25px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 
      0 8px 32px rgba(0, 0, 0, 0.4),
      inset 0 1px 0 rgba(255, 255, 255, 0.1);
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .glass-card-enhanced:hover {
    background: rgba(15, 23, 42, 0.7);
    border: 1px solid rgba(255, 255, 255, 0.15);
    box-shadow: 
      0 16px 48px rgba(0, 0, 0, 0.5),
      inset 0 1px 0 rgba(255, 255, 255, 0.15);
    transform: translateY(-2px);
  }

  /* Tab Styling Enhancement */
  .tabs-list-enhanced {
    background: rgba(15, 23, 42, 0.6);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.08);
    border-radius: 12px;
    padding: 4px;
  }

  /* Activity Feed Styling */
  .activity-item {
    transition: all 0.2s ease;
  }

  .activity-item:hover {
    background: rgba(255, 255, 255, 0.05);
    transform: translateX(4px);
  }
}

@keyframes float {
  0%, 100% { 
    transform: translateY(0px); 
  }
  50% { 
    transform: translateY(-20px); 
  }
}

@keyframes glow {
  from { 
    box-shadow: 0 0 20px rgba(99, 102, 241, 0.3); 
  }
  to { 
    box-shadow: 0 0 30px rgba(99, 102, 241, 0.6); 
  }
}

@keyframes slideUp {
  from { 
    opacity: 0; 
    transform: translateY(50px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

@keyframes shimmer {
  0% { background-position: -200% 0; }
  100% { background-position: 200% 0; }
}

@keyframes fadeIn {
  from { 
    opacity: 0; 
  }
  to { 
    opacity: 1; 
  }
}

@keyframes bounceIn {
  0% { 
    opacity: 0; 
    transform: scale(0.3); 
  }
  50% { 
    opacity: 1; 
    transform: scale(1.05); 
  }
  70% { 
    transform: scale(0.9); 
  }
  100% { 
    opacity: 1; 
    transform: scale(1); 
  }
}

@keyframes scaleIn {
  from { 
    opacity: 0; 
    transform: scale(0.8); 
  }
  to { 
    opacity: 1; 
    transform: scale(1); 
  }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: hsl(217, 33%, 17%);
}

::-webkit-scrollbar-thumb {
  background: hsl(224, 71.4%, 56.1%);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(224, 71.4%, 46.1%);
}

/* Loading animations */
.loading-shimmer {
  background: linear-gradient(
    90deg,
    rgba(255, 255, 255, 0.1) 0%,
    rgba(255, 255, 255, 0.2) 50%,
    rgba(255, 255, 255, 0.1) 100%
  );
  background-size: 200% 100%;
  animation: shimmer 2s infinite;
}

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

/* 3D transforms */
.transform-3d {
  transform-style: preserve-3d;
}

.rotate-y-12 {
  transform: rotateY(12deg);
}

.rotate-x-12 {
  transform: rotateX(12deg);
}

/* Custom button animations */
.btn-primary {
  @apply bg-gradient-to-r from-primary to-purple-500 text-primary-foreground px-6 py-3 rounded-lg font-semibold transition-all duration-300 hover:shadow-xl hover:scale-105;
}

.btn-secondary {
  @apply glass-effect text-foreground px-6 py-3 rounded-lg font-semibold transition-all duration-300 hover:bg-slate-700/50;
}

/* Monaco Editor theme adjustments */
.monaco-editor {
  background: hsl(222, 84%, 4.9%) !important;
}

/* AdSense optimized layouts */
.ad-container {
  @apply min-h-[250px] flex items-center justify-center bg-gray-100/20 dark:bg-gray-800/20 rounded-lg border border-border;
}

.ad-container::before {
  content: "Advertisement";
  @apply text-muted-foreground text-sm;
}

/* Enhanced Mobile Responsiveness */
@media (max-width: 768px) {
  .glass-effect {
    background: rgba(15, 23, 42, 0.98);
    backdrop-filter: blur(15px);
  }

  .grid-mobile-stack {
    grid-template-columns: 1fr !important;
  }

  .mobile-full-width {
    width: 100% !important;
    margin: 0 !important;
  }

  .mobile-text-base {
    font-size: 1rem !important;
    line-height: 1.5rem !important;
  }

  .mobile-padding {
    padding: 1rem !important;
  }

  .mobile-gap-4 {
    gap: 1rem !important;
  }
}

/* Touch-friendly interactions */
@media (hover: none) and (pointer: coarse) {
  .touch-target {
    min-height: 44px;
    min-width: 44px;
  }

  .hover\:scale-105:hover {
    transform: none;
  }

  .hover\:bg-slate-700:hover {
    background-color: rgb(51 65 85 / 0.5);
  }
}

/* Enhanced animations for better UX */
.fade-in-up {
  animation: fadeInUp 0.6s ease-out;
}

.slide-in-left {
  animation: slideInLeft 0.5s ease-out;
}

.slide-in-right {
  animation: slideInRight 0.5s ease-out;
}

.pulse-glow {
  animation: pulseGlow 2s ease-in-out infinite;
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes pulseGlow {
  0%, 100% {
    box-shadow: 0 0 10px rgba(99, 102, 241, 0.3);
  }
  50% {
    box-shadow: 0 0 20px rgba(99, 102, 241, 0.6);
  }
}

/* Enhanced focus states for accessibility */
.focus-ring:focus {
  outline: 2px solid hsl(224, 71.4%, 56.1%);
  outline-offset: 2px;
}

/* Improved button hover states */
.btn-primary:hover {
  background: linear-gradient(135deg, hsl(224, 71.4%, 46.1%), hsl(262, 83%, 60%));
  transform: translateY(-1px);
  box-shadow: 0 8px 25px rgba(99, 102, 241, 0.3);
}

.btn-primary:active {
  transform: translateY(0);
  box-shadow: 0 4px 15px rgba(99, 102, 241, 0.2);
}

/* Template card animations */
.template-card {
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

.template-card:hover {
  background: rgba(51, 65, 85, 0.7);
  border-color: hsl(224, 71.4%, 56.1%);
  transform: translateX(4px);
}

.template-card:active {
  transform: translateX(2px) scale(0.98);
}

/* Dragging states */
.dragging {
  opacity: 0.5;
  transform: rotate(5deg);
  z-index: 1000;
}

.drag-over {
  border-color: hsl(224, 71.4%, 56.1%) !important;
  background: rgba(99, 102, 241, 0.1) !important;
}

/* Enhanced tooltip styles */
.tooltip-content {
  background: rgba(15, 23, 42, 0.95);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
}

/* Progress bar enhancements */
.progress-enhanced {
  background: linear-gradient(90deg,
    hsl(224, 71.4%, 56.1%) 0%,
    hsl(262, 83%, 70%) 50%,
    hsl(189, 94%, 57%) 100%
  );
  background-size: 200% 100%;
  animation: progressShimmer 2s linear infinite;
}

@keyframes progressShimmer {
  0% { background-position: -200% 0; }
  100% { background-position: 200% 0; }
}

/* Loading state improvements */
.loading-pulse {
  animation: loadingPulse 1.5s ease-in-out infinite;
}

@keyframes loadingPulse {
  0%, 100% {
    opacity: 0.6;
  }
  50% {
    opacity: 1;
  }
}

/* Image grid enhancements */
.image-grid-item {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  overflow: hidden;
}

.image-grid-item:hover {
  transform: translateY(-4px);
  box-shadow: 0 12px 25px rgba(0, 0, 0, 0.4);
}

.image-grid-item:hover .image-overlay {
  opacity: 1;
}

.image-overlay {
  background: linear-gradient(135deg, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 100%);
  transition: opacity 0.3s ease;
  opacity: 0;
}

/* Enhanced selection states */
.selected-state {
  border-color: hsl(224, 71.4%, 56.1%) !important;
  box-shadow: 0 0 0 2px rgba(99, 102, 241, 0.2) !important;
}

