            {/* AI Services Dropdown */}
            {isAuthenticated && (
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="ghost" className="flex items-center gap-1">
                    AI Services
                    <ChevronDown className="w-4 h-4" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end" className="glass-effect">
                  <DropdownMenuLabel>AI Services</DropdownMenuLabel>
                  <DropdownMenuSeparator />
                  {aiServices.map((service) => (
                    <DropdownMenuItem key={service.name} asChild>
                      <a href={service.href} className="cursor-pointer">
                        {service.name}
                      </a>
                    </DropdownMenuItem>
                  ))}
                </DropdownMenuContent>
              </DropdownMenu>
            )}
          </div>

          {/* Auth Section */}
          <div className="flex items-center space-x-4">
            {isLoading ? (
              <div className="w-8 h-8 rounded-full bg-muted animate-pulse" />
            ) : isAuthenticated && user ? (
              <>
                {/* Usage Badge */}
                <Badge variant="secondary" className="hidden sm:flex">
                  {user.generationsUsed}/{user.generationsLimit === -1 ? 'âˆž' : user.generationsLimit}
                </Badge>

                {/* User Menu */}
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" className="flex items-center space-x-2">
                      {user.profileImageUrl ? (
                        <img 
                          src={user.profileImageUrl} 
                          alt="Profile" 
                          className="w-8 h-8 rounded-full object-cover"
                        />
                      ) : (
                        <div className="w-8 h-8 rounded-full bg-gradient-to-r from-primary to-purple-500 flex items-center justify-center">
                          <User className="w-4 h-4 text-white" />
                        </div>
                      )}
                      <span className="hidden sm:block">
                        {user.firstName || 'Account'}
                      </span>
                      <ChevronDown className="w-4 h-4" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end" className="glass-effect w-56">
                    <DropdownMenuLabel className="flex items-center gap-2">
                      <div className="flex flex-col">
                        <span>{user.firstName} {user.lastName}</span>
                        <span className="text-xs text-muted-foreground">{user.email}</span>
                      </div>
                      {user.subscriptionTier === 'premium' && (
                        <Crown className="w-4 h-4 text-yellow-500" />
                      )}
                    </DropdownMenuLabel>
                    <DropdownMenuSeparator />

                    <DropdownMenuItem asChild>
                      <a href="/" className="cursor-pointer">
                        Dashboard
                      </a>
                    </DropdownMenuItem>

                    <DropdownMenuItem asChild>
                      <a href="/pricing" className="cursor-pointer">
                        <Settings className="w-4 h-4 mr-2" />
                        Subscription
                      </a>
                    </DropdownMenuItem>

                    {user.isAdmin && (
                      <DropdownMenuItem asChild>
                        <a href="/admin" className="cursor-pointer">
                          <Shield className="w-4 h-4 mr-2" />
                          Admin Panel
                        </a>
                      </DropdownMenuItem>
                    )}

                    <DropdownMenuSeparator />
                    <DropdownMenuItem 
                      onClick={() => {
                        const signOut = async () => {
                          try {
                            // Clear local auth data first
                            localStorage.removeItem('supabase-auth-token');
                            document.cookie = 'supabase-auth-token=; Path=/; Expires=Thu, 01 Jan 1970 00:00:01 GMT; Domain=.loveaihub.com';

                            const response = await fetch('/api/auth/signout', {
                              method: 'POST',
                              credentials: 'include',
                              headers: { 'Content-Type': 'application/json' }
                            });

                            // Force complete page reload to reset all state
                            window.location.replace('/');
                          } catch (error) {
                            console.error('Logout error:', error);
                            // Force reload even on error to clear state
                            window.location.replace('/');
                          }
                        };
                        signOut();
                      }}
                      className="cursor-pointer text-destructive"
                    >
                      <LogOut className="w-4 h-4 mr-2" />
                      Sign Out
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </>
            ) : (
              <div className="flex items-center space-x-3">
                <Button variant="ghost" asChild>
                  <a href="/api/login">Sign In</a>
                </Button>
                <Button className="btn-depth" asChild>
                  <a href="/api/login">Start Free</a>
                </Button>
              </div>
            )}

            {/* Mobile Menu */}
            <Sheet open={isOpen} onOpenChange={setIsOpen}>
              <SheetTrigger asChild>
                <Button variant="ghost" size="sm" className="md:hidden">
                  <Menu className="w-5 h-5" />
                </Button>
              </SheetTrigger>
              <SheetContent side="right" className="glass-effect w-80">
                <div className="flex flex-col space-y-4 mt-8">
                  {/* Navigation Items */}
                  {navigationItems.map((item) => (
                    <a
                      key={item.name}
                      href={item.href}
                      className="text-foreground hover:text-primary transition-colors p-2 rounded-lg hover:bg-muted"
                      onClick={() => setIsOpen(false)}
                    >
                      {item.name}
                    </a>
                  ))}

                  {/* AI Services for Mobile */}
                  {isAuthenticated && (
                    <>
                      <div className="border-t border-border pt-4 mt-4">
                        <p className="text-sm font-semibold text-muted-foreground mb-2">AI Services</p>
                        {aiServices.map((service) => (
                          <a
                            key={service.name}
                            href={service.href}
                            className="block text-foreground hover:text-primary transition-colors p-2 rounded-lg hover:bg-muted"
                            onClick={() => setIsOpen(false)}
                          >
                            {service.name}
                          </a>
                        ))}
                      </div>
                    </>
                  )}

                  {/* Auth Section for Mobile */}
                  {!isAuthenticated && (
                    <div className="border-t border-border pt-4 mt-4 space-y-2">
                      <Button variant="ghost" className="w-full justify-start" asChild>
                        <a href="/api/login" onClick={() => setIsOpen(false)}>
                          Sign In
                        </a>
                      </Button>
                      <Button className="w-full btn-depth" asChild>
                        <a href="/api/login" onClick={() => setIsOpen(false)}>
                          Start Free
                        </a>
                      </Button>
                    </div>
                  )}
                </div>
              </SheetContent>
            </Sheet>
          </div>
        </div>
      </div>
    </nav>
  );
}

