import { createClient } from '@supabase/supabase-js';
import type { Express, RequestHandler } from "express";
import session from "express-session";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

// Supabase configuration with graceful fallbacks for serverless
let supabase: any = null;

function initializeSupabase() {
  if (!supabase) {
    try {
      // Use environment variables with secure fallbacks for Replit migration
      const supabaseUrl = process.env.SUPABASE_URL || 'https://gfrpidhedgqixkgafumc.supabase.co';
      const supabaseAnonKey = process.env.SUPABASE_ANON_KEY || 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImdmcnBpZGhlZGdxaXhrZ2FmdW1jIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTM1ODM0NjgsImV4cCI6MjA2OTE1OTQ2OH0.JaYdiISBG8vqfen_qzkOVgYRBq4V2v5CzvxjhBBsM9c';

      supabase = createClient(supabaseUrl, supabaseAnonKey);
      console.log('Supabase initialized successfully');
    } catch (error) {
      console.error("Failed to initialize Supabase:", error);
    }
  }
  return supabase;
}

export { supabase };
export { initializeSupabase };

export function getSession() {
  try {
    const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week

    // For development, always use memory store to avoid database session issues
    if (process.env.NODE_ENV !== "production") {
      console.log("Using simplified memory store for sessions in development");

      // Use proper session configuration for development
      return session({
        secret: "loveaihub-session-secret-dev",
        resave: false,
        saveUninitialized: false, // Only save sessions when data is added
        name: "connect.sid", // Use default session cookie name
        cookie: {
          httpOnly: true, // Secure cookie handling
          secure: false, // Allow over HTTP in development
          sameSite: 'lax', // Allow cross-origin requests
          maxAge: 24 * 60 * 60 * 1000, // 24 hours
          path: '/', // Make cookie available for entire site
        },
      });
    }

    // Use DATABASE_URL with secure fallback for production
    const databaseUrl = process.env.DATABASE_URL || 'postgresql://postgres.gfrpidhedgqixkgafumc:AKraj%40%245630@aws-0-ap-south-1.pooler.supabase.com:6543/postgres';

    if (!databaseUrl) {
      console.warn("DATABASE_URL not available, using memory store");
      const MemoryStore = session.MemoryStore;
      return session({
        secret: process.env.SESSION_SECRET || "8788fdfd5215934707e38407bcb2920b2aa6716b60801fec6ab1ff6ed34cf6d7",
        store: new MemoryStore(),
        resave: false,
        saveUninitialized: false,
        cookie: {
          httpOnly: true,
          secure: process.env.NODE_ENV === "production",
          maxAge: sessionTtl,
        },
      });
    }

    const pgStore = connectPg(session);
    const sessionStore = new pgStore({
      conString: databaseUrl,
      createTableIfMissing: false,
      ttl: sessionTtl,
      tableName: "sessions",
    });

    return session({
      secret: process.env.SESSION_SECRET || "8788fdfd5215934707e38407bcb2920b2aa6716b60801fec6ab1ff6ed34cf6d7",
      store: sessionStore,
      resave: false,
      saveUninitialized: false,
      cookie: {
        httpOnly: true,
        secure: process.env.NODE_ENV === "production",
        maxAge: sessionTtl,
      },
