import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";

export default function Debug() {
  const [testResults, setTestResults] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const addResult = (test: string, result: any) => {
    setTestResults(prev => [...prev, { test, result, timestamp: new Date().toISOString() }]);
  };

  const testA4FConnection = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('/api/test-a4f');
      const result = await response.json();
      addResult('A4F Connection Test', result);

      if (result.success) {
        toast({
          title: "A4F Connection Test",
          description: "âœ… A4F API connection successful",
        });
      } else {
        toast({
          title: "A4F Connection Test",
          description: "âŒ A4F API connection failed",
          variant: "destructive"
        });
      }
    } catch (error) {
      addResult('A4F Connection Test', { error: error.message });
      toast({
        title: "A4F Connection Test",
        description: "âŒ Test failed with error",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  const testAuthentication = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('/api/test-auth', {
        credentials: 'include'
      });
      const result = await response.json();
      addResult('Authentication Test', result);

      if (result.success) {
        toast({
          title: "Authentication Test",
          description: "âœ… Authentication successful",
        });
      } else {
        toast({
          title: "Authentication Test",
          description: "âŒ Authentication failed",
          variant: "destructive"
        });
      }
    } catch (error) {
      addResult('Authentication Test', { error: error.message });
      toast({
        title: "Authentication Test",
        description: "âŒ Authentication test failed",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  const testImageGeneration = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('/api/test-image-generation', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include'
      });
      const result = await response.json();
      addResult('Image Generation Test', result);

      if (result.success) {
        toast({
          title: "Image Generation Test",
          description: "âœ… Image generation successful",
        });
      } else {
        toast({
          title: "Image Generation Test",
          description: "âŒ Image generation failed",
          variant: "destructive"
        });
      }
    } catch (error) {
      addResult('Image Generation Test', { error: error.message });
      toast({
        title: "Image Generation Test",
        description: "âŒ Image generation test failed",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  const clearResults = () => {
    setTestResults([]);
  };

  return (
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="mb-8">
            <h1 className="text-4xl font-bold mb-4">
              <span className="gradient-text">Debug & Testing</span>
            </h1>
