client/src/components/dashboard/recent-a.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { 
  Clock, 
  Image as ImageIcon, 
  Video, 
  MessageSquare, 
  Mic,
  Download,
  Share,
  Heart,
  MoreHorizontal,
  TrendingUp,
  Calendar,
  Edit
} from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import { useRecentActivity } from "@/hooks/useDashboardData";

export default function RecentActivity() {
  const { data: activities, isLoading } = useRecentActivity(10);
  const [likedItems, setLikedItems] = useState<Set<string>>(new Set());

  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'image': return ImageIcon;
      case 'video': return Video;
      case 'chat': return MessageSquare;
      case 'audio': return Mic;
      case 'transcription': return Mic;
      case 'image_edit': return Edit;
      default: return Clock;
    }
  };

  const getActivityTitle = (activity: any) => {
    const typeMap = {
      'image': 'Image Generation',
      'video': 'Video Creation', 
      'chat': 'AI Chat Session',
      'audio': 'Audio Generation',
      'transcription': 'Audio Transcription',
      'image_edit': 'Image Editing'
    };
    return typeMap[activity.type as keyof typeof typeMap] || 'AI Generation';
  };

  const getActivityDescription = (activity: any) => {
    if (activity.prompt) {
      return activity.prompt.length > 50 
        ? `${activity.prompt.substring(0, 50)}...` 
        : activity.prompt;
    }
    return `Using ${activity.model} model`;
  };

  const getActivityColor = (type: string) => {
    switch (type) {
      case 'image': return 'bg-gradient-to-br from-purple-500 to-pink-500';
      case 'video': return 'bg-gradient-to-br from-red-500 to-orange-500';
      case 'chat': return 'bg-gradient-to-br from-green-500 to-teal-500';
      case 'audio': return 'bg-gradient-to-br from-blue-500 to-cyan-500';
      case 'transcription': return 'bg-gradient-to-br from-indigo-500 to-purple-500';
      case 'image_edit': return 'bg-gradient-to-br from-yellow-500 to-orange-500';
      default: return 'bg-gradient-to-br from-gray-500 to-gray-600';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'text-green-500';
      case 'processing': return 'text-yellow-500';
      case 'failed': return 'text-red-500';
      default: return 'text-gray-500';
    }
  };

  const handleToggleLike = (id: string) => {
    setLikedItems(prev => {
      const newLiked = new Set(prev);
      if (newLiked.has(id)) {
        newLiked.delete(id);
      } else {
        newLiked.add(id);
      }
      return newLiked;
    });
  };

  return (
    <Card className="glass-card">
      <CardHeader className="flex flex-row items-center justify-between">
        <div className="space-y-1">
          <CardTitle className="flex items-center gap-2">
            <TrendingUp className="w-5 h-5 text-primary" />
            Recent Activity
          </CardTitle>
          <p className="text-sm text-muted-foreground">
            Your latest AI generations and activities
          </p>
        </div>
        <Button variant="outline" size="sm" className="flex items-center gap-2">
          <Calendar className="w-4 h-4" />
          View All
        </Button>
      </CardHeader>

      <CardContent className="pt-0">
        {isLoading ? (
          <div className="space-y-4">
            {Array.from({ length: 5 }).map((_, i) => (
              <div key={i} className="flex items-start space-x-3 p-3 rounded-lg animate-pulse">
                <div className="w-10 h-10 bg-muted rounded-full"></div>
                <div className="flex-1 space-y-2">
                  <div className="h-4 bg-muted rounded w-3/4"></div>
                  <div className="h-3 bg-muted rounded w-1/2"></div>
                  <div className="flex space-x-2">
                    <div className="h-4 bg-muted rounded w-16"></div>
                    <div className="h-4 bg-muted rounded w-16"></div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : activities && activities.length > 0 ? (
          <div className="space-y-4 max-h-96 overflow-y-auto custom-scrollbar">
            {activities.map((activity) => {
              const Icon = getActivityIcon(activity.type);
              const isLiked = likedItems.has(activity.id);
              return (
                <div 
                  key={activity.id} 
                  className="flex items-start space-x-3 p-3 rounded-lg hover:bg-accent/20 transition-colors group"
                >
                  <Avatar className={`w-10 h-10 ${getActivityColor(activity.type)} flex items-center justify-center`}>
                    <AvatarFallback className={`${getActivityColor(activity.type)} border-0`}>
                      <Icon className="w-5 h-5 text-white" />
                    </AvatarFallback>
                  </Avatar>

                  <div className="flex-1 min-w-0 space-y-1">
                    <div className="flex items-start justify-between">
                      <div className="flex-1 min-w-0">
                        <p className="text-sm font-medium truncate group-hover:text-primary transition-colors">
                          {getActivityTitle(activity)}
                        </p>
                        <p className="text-xs text-muted-foreground">
                          {getActivityDescription(activity)}
                        </p>
                        <div className="flex items-center space-x-2 mt-1">
                          <Badge variant="outline" className="text-xs">
                            {activity.model}
                          </Badge>
                          <span className={`text-xs font-medium ${getStatusColor(activity.status)}`}>
                            {activity.status}
                          </span>
                          <span className="text-xs text-muted-foreground">
                            {formatDistanceToNow(new Date(activity.createdAt), { addSuffix: true })}
                          </span>
                        </div>
                      </div>

                      <div className="flex items-center space-x-1 opacity-0 group-hover:opacity-100 transition-opacity ml-2">
                        <Button
                          variant="ghost"
                          size="sm"
                          className={`h-7 w-7 p-0 ${isLiked ? 'text-red-500' : 'text-muted-foreground'}`}
                          onClick={() => handleToggleLike(activity.id)}
                        >
                          <Heart className={`w-3 h-3 ${isLiked ? 'fill-current' : ''}`} />
                        </Button>
                        <Button variant="ghost" size="sm" className="h-7 w-7 p-0 text-muted-foreground">
                          <Share className="w-3 h-3" />
                        </Button>
                        <Button variant="ghost" size="sm" className="h-7 w-7 p-0 text-muted-foreground">
                          <Download className="w-3 h-3" />
                        </Button>
                        <Button variant="ghost" size="sm" className="h-7 w-7 p-0 text-muted-foreground">
                          <MoreHorizontal className="w-3 h-3" />
                        </Button>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <div className="text-center py-8">
            <Clock className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
            <p className="text-muted-foreground mb-2">No recent activity</p>
            <p className="text-sm text-muted-foreground">
              Start creating with AI tools to see your activity here
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/dashboard/usage-an.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend, 
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  LineChart,
  Line
} from 'recharts';
import { 
  BarChart3, 
  TrendingUp, 
  Calendar,
  Filter
} from "lucide-react";
import { useUserAnalytics } from "@/hooks/useDashboardData";

const COLORS = ['#3b82f6', '#8b5cf6', '#10b981', '#f59e0b', '#ef4444', '#6b7280'];

export default function UsageAnalytics() {
  const [selectedPeriod, setSelectedPeriod] = useState("7days");
  const [activeChart, setActiveChart] = useState<'daily' | 'models'>('daily');
  const { data: analytics, isLoading } = useUserAnalytics(selectedPeriod);

  // Transform API data for daily usage chart
  const dailyData = analytics?.dailyStats ? 
    analytics.dailyStats.reduce((acc: any[], item: any) => {
      const existingDate = acc.find(d => d.date === item.date);
      if (existingDate) {
        existingDate[item.type] = (existingDate[item.type] || 0) + item.count;
        existingDate.total += item.count;
      } else {
        acc.push({
          date: item.date,
          [item.type]: item.count,
          total: item.count,
          name: new Date(item.date).toLocaleDateString('en-US', { 
            month: 'short', 
            day: 'numeric' 
          })
        });
      }
      return acc;
    }, []).sort((a: any, b: any) => new Date(a.date).getTime() - new Date(b.date).getTime()) : [];

  // Transform API data for model usage pie chart
  const modelData = analytics?.modelStats?.slice(0, 6).map((item: any, index: number) => ({
    name: item.model,
    value: item.count,
    color: COLORS[index % COLORS.length]
  })) || [];

  const CustomTooltip = ({ active, payload, label }: any) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-background/95 border border-border rounded-lg p-3 shadow-lg backdrop-blur-sm">
          <p className="text-sm font-medium mb-1">{label}</p>
          {payload.map((entry: any, index: number) => (
            <p key={index} className="text-xs" style={{ color: entry.color }}>
              {`${entry.dataKey}: ${entry.value}`}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  if (isLoading) {
    return (
      <Card className="glass-card">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div className="space-y-2">
              <div className="h-6 bg-muted rounded w-48 animate-pulse"></div>
              <div className="h-4 bg-muted rounded w-32 animate-pulse"></div>
            </div>
            <div className="h-9 bg-muted rounded w-24 animate-pulse"></div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="h-80 bg-muted rounded animate-pulse"></div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="glass-card">
      <CardHeader className="flex flex-row items-center justify-between">
        <div className="space-y-1">
          <CardTitle className="flex items-center gap-2">
            <BarChart3 className="w-5 h-5 text-primary" />
            Usage Analytics
          </CardTitle>
          <p className="text-sm text-muted-foreground">
            Track your AI generation patterns and model usage
          </p>
        </div>
        <div className="flex items-center gap-2">
          <Button
            variant="outline" 
            size="sm"
            className="flex items-center gap-2"
            onClick={() => setSelectedPeriod(selectedPeriod === "7days" ? "30days" : "7days")}
          >
            <Calendar className="w-4 h-4" />
            {selectedPeriod === "7days" ? "7 Days" : "30 Days"}
          </Button>
          <Button
            variant="outline"
            size="sm" 
            className="flex items-center gap-2"
            onClick={() => setActiveChart(activeChart === 'daily' ? 'models' : 'daily')}
          >
            <Filter className="w-4 h-4" />
            {activeChart === 'daily' ? 'Daily Usage' : 'Model Usage'}
          </Button>
        </div>
      </CardHeader>

      <CardContent className="pt-0">
        {!analytics || (dailyData.length === 0 && modelData.length === 0) ? (
          <div className="text-center py-12">
            <BarChart3 className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
            <p className="text-muted-foreground mb-2">No usage data available</p>
            <p className="text-sm text-muted-foreground">
              Start using AI tools to see your analytics here
            </p>
          </div>
        ) : (
          <div className="space-y-6">
            {/* Period Summary */}
            <div className="flex items-center justify-between p-4 bg-accent/10 rounded-lg">
              <div>
                <p className="text-sm text-muted-foreground">Total Generations</p>
                <p className="text-2xl font-bold">
                  {dailyData.reduce((sum, day) => sum + day.total, 0)}
                </p>
              </div>
              <div className="text-right">
                <p className="text-sm text-muted-foreground">Period</p>
                <p className="font-medium">{selectedPeriod === "7days" ? "Last 7 Days" : "Last 30 Days"}</p>
              </div>
            </div>

            {/* Chart Display */}
            <div className="h-80">
              <ResponsiveContainer width="100%" height="100%">
                {activeChart === 'daily' ? (
                  dailyData.length > 0 ? (
                    <BarChart data={dailyData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
                      <CartesianGrid strokeDasharray="3 3" className="opacity-30" />
                      <XAxis 
                        dataKey="name" 
                        className="text-xs"
                        tick={{ fill: 'hsl(var(--muted-foreground))' }}
                      />
                      <YAxis 
                        className="text-xs"
                        tick={{ fill: 'hsl(var(--muted-foreground))' }}
                      />
                      <Tooltip content={<CustomTooltip />} />
                      <Legend />
                      <Bar dataKey="image" fill="#3b82f6" name="Images" />
                      <Bar dataKey="video" fill="#8b5cf6" name="Videos" />
                      <Bar dataKey="chat" fill="#10b981" name="Chat" />
                      <Bar dataKey="audio" fill="#f59e0b" name="Audio" />
                    </BarChart>
                  ) : (
                    <div className="flex items-center justify-center h-full">
                      <p className="text-muted-foreground">No daily usage data</p>
                    </div>
                  )
                ) : (
                  modelData.length > 0 ? (
                    <PieChart margin={{ top: 20, right: 30, left: 20, bottom: 20 }}>
                      <Pie
                        data={modelData}
                        cx="50%"
                        cy="50%"
                        outerRadius={120}
                        fill="#8884d8"
                        dataKey="value"
                        label={({ name, value, percent }) => 
                          `${name}: ${value} (${(percent * 100).toFixed(0)}%)`
                        }
                        className="text-xs"
                      >
                        {modelData.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={entry.color} />
                        ))}
                      </Pie>
                      <Tooltip
                        formatter={(value: any) => [value, 'Generations']}
                        contentStyle={{
                          backgroundColor: 'hsl(var(--background))',
                          border: '1px solid hsl(var(--border))',
                          borderRadius: '8px'
                        }}
                      />
                    </PieChart>
                  ) : (
                    <div className="flex items-center justify-center h-full">
                      <p className="text-muted-foreground">No model usage data</p>
                    </div>
                  )
                )}
              </ResponsiveContainer>
            </div>

            {/* Model Stats Summary */}
            {modelData.length > 0 && (
              <div className="space-y-3">
                <h4 className="text-sm font-medium flex items-center gap-2">
                  <TrendingUp className="w-4 h-4" />
                  Top Models
                </h4>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                  {modelData.slice(0, 6).map((model, index) => (
                    <div key={model.name} className="flex items-center gap-2 p-2 rounded-lg bg-accent/10">
                      <div 
                        className="w-3 h-3 rounded-full flex-shrink-0" 
                        style={{ backgroundColor: model.color }}
                      ></div>
                      <div className="min-w-0 flex-1">
                        <p className="text-sm font-medium truncate">{model.name}</p>
                        <p className="text-xs text-muted-foreground">{model.value} uses</p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

