                      ? `bg-gradient-to-br ${item.gradient} text-white shadow-lg hover:shadow-xl` 
                      : 'bg-gradient-to-br from-accent/20 to-accent/10 group-hover:from-accent/40 group-hover:to-accent/20 group-hover:shadow-md'
