} from "lucide-react";

export default function SubscriptionManager() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isUpgrading, setIsUpgrading] = useState(false);

  const subscriptionMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", "/api/subscribe", {});
      return await response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Subscription Created",
        description: "Redirecting to payment page...",
      });
      // In a real implementation, redirect to Razorpay payment page
      console.log("Subscription data:", data);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Subscription Error",
        description: error.message || "Failed to create subscription.",
        variant: "destructive",
      });
    },
  });

  const handleUpgrade = () => {
    setIsUpgrading(true);
    subscriptionMutation.mutate();
  };

  const isPremium = user?.subscriptionType === "premium";
  const usagePercentage = user?.subscriptionType === "free" 
    ? (user.generationsUsed / user.generationsLimit) * 100 
    : 0;

  const features = {
    free: [
      "50 generations per month",
      "Standard quality",
      "Community support",
      "Basic API access"
    ],
    premium: [
      "Unlimited generations",
      "HD quality & priority processing",
      "Advanced AI chat with tools",
      "Full API access",
      "Priority support",
      "Advanced analytics"
    ]
  };

  return (
    <div className="space-y-6">
      {/* Current Plan Status */}
      <Card className="glass-effect border-border">
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            {isPremium ? (
              <Crown className="w-6 h-6 text-yellow-400" />
            ) : (
              <Zap className="w-6 h-6 text-primary" />
