client/src/components/ai/audio-tools.tsx.content:
import { useState, useRef } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  Volume2, 
  FileAudio, 
  Mic,
  Upload,
  Download, 
  Play,
  Pause,
  Loader2,
  Settings
} from "lucide-react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";

interface AudioToolsProps {
  type: 'tts' | 'transcription';
  selectedModel: string;
  onModelChange: (model: string) => void;
  models: Array<{
    id: string;
    name: string;
    provider: string;
    category: string;
    description: string;
    badge: string;
    badgeColor: string;
    voices?: string[];
    languages?: string[];
  }>;
}

export default function AudioTools({ type, selectedModel, onModelChange, models }: AudioToolsProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const fileInputRef = useRef<HTMLInputElement>(null);
  const audioRef = useRef<HTMLAudioElement>(null);

  // TTS States
  const [text, setText] = useState("");
  const [selectedVoice, setSelectedVoice] = useState("alloy");
  const [generatedAudio, setGeneratedAudio] = useState<string | null>(null);
  const [isPlaying, setIsPlaying] = useState(false);

  // Transcription States
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [selectedLanguage, setSelectedLanguage] = useState("auto");
  const [transcriptionResult, setTranscriptionResult] = useState<string>("");

  const currentModel = models.find(m => m.id === selectedModel);

  // TTS Mutation
  const ttsMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/ai/audio/generate", data);
      return response.json();
    },
    onSuccess: (data) => {
      if (data.result?.url) {
        setGeneratedAudio(data.result.url);
      }
      toast({
        title: "Audio generated successfully!",
        description: "Your text has been converted to speech",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/generations"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      toast({
        title: "Generation failed",
        description: error.message || "Failed to generate audio",
        variant: "destructive",
      });
    },
  });

  // Transcription Mutation
  const transcriptionMutation = useMutation({
    mutationFn: async (data: any) => {
      const formData = new FormData();
      formData.append('file', data.file);
      formData.append('model', data.model);
      if (data.language) {
        formData.append('language', data.language);
      }

      const response = await fetch('/api/ai/audio/transcribe', {
        method: 'POST',
        body: formData,
        credentials: 'include',
      });

      if (!response.ok) {
        const text = await response.text();
        throw new Error(`${response.status}: ${text}`);
      }

      return response.json();
    },
    onSuccess: (data) => {
      setTranscriptionResult(data.result?.text || data.text || "No transcription available");
      toast({
        title: "Transcription completed!",
        description: "Your audio has been transcribed successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/generations"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      toast({
        title: "Transcription failed",
        description: error.message || "Failed to transcribe audio",
        variant: "destructive",
      });
    },
  });

  const handleTTSGenerate = () => {
    if (!text.trim()) {
      toast({
        title: "Text required",
        description: "Please enter text to convert to speech",
        variant: "destructive",
      });
      return;
    }

    if (user?.subscriptionTier === 'free' && user.generationsUsed >= user.generationsLimit) {
      toast({
        title: "Generation limit reached",
        description: "Upgrade to Premium for unlimited generations",
        variant: "destructive",
      });
      return;
    }

    ttsMutation.mutate({
      model: selectedModel,
      input: text.trim(),
      voice: selectedVoice,
    });
  };

  const handleTranscribe = () => {
    if (!selectedFile) {
      toast({
        title: "File required",
        description: "Please select an audio file to transcribe",
        variant: "destructive",
      });
      return;
    }

    if (user?.subscriptionTier === 'free' && user.generationsUsed >= user.generationsLimit) {
      toast({
        title: "Generation limit reached",
        description: "Upgrade to Premium for unlimited transcriptions",
        variant: "destructive",
      });
      return;
    }

    transcriptionMutation.mutate({
      model: selectedModel,
      file: selectedFile,
      language: selectedLanguage !== 'auto' ? selectedLanguage : undefined,
    });
  };

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Check file size (max 25MB)
      if (file.size > 25 * 1024 * 1024) {
        toast({
          title: "File too large",
          description: "Please select a file smaller than 25MB",
          variant: "destructive",
        });
        return;
      }

      // Check file type
      const validTypes = ['audio/mpeg', 'audio/wav', 'audio/m4a', 'audio/flac', 'audio/ogg'];
      if (!validTypes.includes(file.type)) {
        toast({
          title: "Invalid file type",
          description: "Please select an MP3, WAV, M4A, FLAC, or OGG file",
          variant: "destructive",
        });
        return;
      }

      setSelectedFile(file);
    }
  };

  const togglePlayback = () => {
    if (!audioRef.current) return;

    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setIsPlaying(!isPlaying);
  };

  const downloadAudio = (url: string, filename: string) => {
    const link = document.createElement('a');
    link.href = url;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const copyTranscription = () => {
    navigator.clipboard.writeText(transcriptionResult);
    toast({
      title: "Copied to clipboard",
      description: "Transcription copied successfully",
    });
  };

  if (type === 'tts') {
    return (
      <div className="space-y-6">
        {/* TTS Form */}
        <Card className="glass-effect">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Volume2 className="w-5 h-5" />
              Text-to-Speech
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Model Selection */}
            <div className="space-y-2">
              <Label>TTS Model</Label>
              <Select value={selectedModel} onValueChange={onModelChange}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {models.map((model) => (
                    <SelectItem key={model.id} value={model.id}>
                      <div className="flex items-center justify-between w-full">
                        <span>{model.name}</span>
                        <Badge className={`ml-2 ${model.badgeColor}`}>
                          {model.badge}
                        </Badge>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Voice Selection */}
            {currentModel?.voices && (
              <div className="space-y-2">
                <Label>Voice</Label>
                <Select value={selectedVoice} onValueChange={setSelectedVoice}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {currentModel.voices.map((voice) => (
                      <SelectItem key={voice} value={voice}>
                        {voice.charAt(0).toUpperCase() + voice.slice(1)}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            )}

            {/* Text Input */}
            <div className="space-y-2">
              <Label>Text to Convert</Label>
              <Textarea
                placeholder="Enter the text you want to convert to speech..."
                value={text}
                onChange={(e) => setText(e.target.value)}
                rows={6}
                className="resize-none"
              />
              <p className="text-sm text-muted-foreground">
                Character count: {text.length} (max 4000)
              </p>
            </div>

            {/* Generate Button */}
            <Button
              onClick={handleTTSGenerate}
              disabled={ttsMutation.isPending || !text.trim()}
              className="w-full btn-depth"
              size="lg"
            >
              {ttsMutation.isPending ? (
                <>
                  <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                  Generating Audio...
                </>
              ) : (
                <>
                  <Volume2 className="w-5 h-5 mr-2" />
                  Generate Speech
                </>
              )}
            </Button>
          </CardContent>
        </Card>

        {/* Generated Audio */}
        {generatedAudio && (
          <Card className="glass-effect">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <FileAudio className="w-5 h-5" />
                Generated Audio
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex items-center gap-4">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={togglePlayback}
                  >
                    {isPlaying ? (
                      <Pause className="w-4 h-4" />
                    ) : (
                      <Play className="w-4 h-4" />
                    )}
                  </Button>

                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => downloadAudio(generatedAudio, 'generated-speech.mp3')}
                  >
                    <Download className="w-4 h-4 mr-2" />
                    Download
                  </Button>
                </div>

                <audio
                  ref={audioRef}
                  src={generatedAudio}
                  onEnded={() => setIsPlaying(false)}
                  onPlay={() => setIsPlaying(true)}
                  onPause={() => setIsPlaying(false)}
                  controls
                  className="w-full"
                />
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    );
  }

  // Transcription UI
  return (
    <div className="space-y-6">
      {/* Transcription Form */}
      <Card className="glass-effect">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileAudio className="w-5 h-5" />
            Audio Transcription
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Model Selection */}
          <div className="space-y-2">
            <Label>Transcription Model</Label>
            <Select value={selectedModel} onValueChange={onModelChange}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {models.map((model) => (
                  <SelectItem key={model.id} value={model.id}>
                    <div className="flex items-center justify-between w-full">
                      <span>{model.name}</span>
                      <Badge className={`ml-2 ${model.badgeColor}`}>
                        {model.badge}
                      </Badge>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Language Selection */}
          {currentModel?.languages && (
            <div className="space-y-2">
              <Label>Language</Label>
              <Select value={selectedLanguage} onValueChange={setSelectedLanguage}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="auto">Auto-detect</SelectItem>
                  {currentModel.languages.filter(lang => lang !== 'auto').map((language) => (
                    <SelectItem key={language} value={language}>
                      {language.toUpperCase()}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          )}

          {/* File Upload */}
          <div className="space-y-2">
            <Label>Audio File</Label>
            <div
              className="border-2 border-dashed border-border rounded-lg p-8 text-center cursor-pointer hover:border-primary transition-colors"
              onClick={() => fileInputRef.current?.click()}
            >
              <Upload className="w-12 h-12 mx-auto mb-4 text-muted-foreground" />
              {selectedFile ? (
                <div>
                  <p className="font-medium">{selectedFile.name}</p>
                  <p className="text-sm text-muted-foreground">
                    {(selectedFile.size / (1024 * 1024)).toFixed(2)} MB
                  </p>
                </div>
              ) : (
                <div>
                  <p className="font-medium mb-2">Click to upload audio file</p>
                  <p className="text-sm text-muted-foreground">
                    MP3, WAV, M4A, FLAC, OGG (max 25MB)
                  </p>
                </div>
              )}
            </div>
            <input
              ref={fileInputRef}
              type="file"
              accept="audio/*"
              onChange={handleFileSelect}
              className="hidden"
            />
          </div>

          {/* Transcribe Button */}
          <Button
            onClick={handleTranscribe}
            disabled={transcriptionMutation.isPending || !selectedFile}
            className="w-full btn-depth"
            size="lg"
          >
            {transcriptionMutation.isPending ? (
              <>
                <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                Transcribing...
              </>
            ) : (
              <>
                <Mic className="w-5 h-5 mr-2" />
                Transcribe Audio
              </>
            )}
          </Button>
        </CardContent>
      </Card>

      {/* Transcription Result */}
      {transcriptionResult && (
        <Card className="glass-effect">
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle className="flex items-center gap-2">
                <FileAudio className="w-5 h-5" />
                Transcription Result
              </CardTitle>
              <Button
                variant="outline"
                size="sm"
                onClick={copyTranscription}
              >
                Copy Text
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            <div className="bg-muted p-4 rounded-lg">
              <p className="whitespace-pre-wrap">{transcriptionResult}</p>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/ai/chat-interface..content:
import { useState, useRef, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  MessageCircle, 
  Send, 
  Bot,
  User,
  Loader2,
  Trash2,
  Copy,
  RotateCcw,
  Settings
} from "lucide-react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";

interface Message {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
  model?: string;
}

interface ChatInterfaceProps {
  selectedModel: string;
  onModelChange: (model: string) => void;
  models: Array<{
    id: string;
    name: string;
    provider: string;
    category: string;
    description: string;
    badge: string;
    badgeColor: string;
    capabilities: string[];
  }>;
  tools: Array<{
    id: string;
    name: string;
    description: string;
    icon: React.ReactNode;
    enabled: boolean;
  }>;
  websocket: WebSocket | null;
}

export default function ChatInterface({ 
  selectedModel, 
  onModelChange, 
  models, 
  tools, 
  websocket 
}: ChatInterfaceProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      role: 'assistant',
      content: 'Hello! I\'m your AI assistant. I can help you with a wide range of tasks including answering questions, creative writing, analysis, and more. How can I assist you today?',
      timestamp: new Date(),
    }
  ]);
  const [inputMessage, setInputMessage] = useState("");
  const [isTyping, setIsTyping] = useState(false);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // WebSocket message handling
  useEffect(() => {
    if (websocket) {
      websocket.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 'chat_response') {
          setMessages(prev => prev.map(msg => 
            msg.id === data.messageId 
              ? { ...msg, content: data.content }
              : msg
          ));
          setIsTyping(false);
        }
      };
    }
  }, [websocket]);

  const chatMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/ai/chat", data);
      return response.json();
    },
    onSuccess: (data) => {
      const assistantMessage: Message = {
        id: Date.now().toString(),
        role: 'assistant',
        content: data.result.choices[0]?.message?.content || 'No response generated',
        timestamp: new Date(),
        model: selectedModel,
      };

      setMessages(prev => [...prev, assistantMessage]);
      setIsTyping(false);
      queryClient.invalidateQueries({ queryKey: ["/api/generations"] });
    },
    onError: (error) => {
      setIsTyping(false);

      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      toast({
        title: "Chat failed",
        description: error.message || "Failed to send message",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (!inputMessage.trim()) return;

    if (user?.subscriptionTier === 'free' && user.generationsUsed >= user.generationsLimit) {
      toast({
        title: "Generation limit reached",
        description: "Upgrade to Premium for unlimited chat",
        variant: "destructive",
      });
      return;
    }

    const userMessage: Message = {
      id: Date.now().toString(),
      role: 'user',
      content: inputMessage.trim(),
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage("");
    setIsTyping(true);

    const chatMessages = [...messages, userMessage].map(msg => ({
      role: msg.role,
      content: msg.content
    }));

    chatMutation.mutate({
      model: selectedModel,
      messages: chatMessages,
      temperature: 0.7,
      max_tokens: 2000,
    });
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const copyMessage = (content: string) => {
    navigator.clipboard.writeText(content);
    toast({
      title: "Copied to clipboard",
      description: "Message copied successfully",
    });
  };

  const clearChat = () => {
    setMessages([{
      id: '1',
      role: 'assistant',
      content: 'Hello! I\'m your AI assistant. How can I help you today?',
      timestamp: new Date(),
    }]);
  };

  const currentModel = models.find(m => m.id === selectedModel);

  return (
    <div className="space-y-6">
      {/* Chat Header */}
      <Card className="glass-effect">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <MessageCircle className="w-5 h-5" />
              AI Chat
            </CardTitle>
            <div className="flex items-center gap-2">
              <Button variant="outline" size="sm" onClick={clearChat}>
                <Trash2 className="w-4 h-4 mr-2" />
                Clear
              </Button>
              <Button variant="outline" size="sm">
                <Settings className="w-4 h-4" />
              </Button>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          {/* Model Selection */}
          <div className="flex items-center gap-4 mb-4">
            <div className="flex-1">
              <Select value={selectedModel} onValueChange={onModelChange}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {models.map((model) => (
                    <SelectItem key={model.id} value={model.id}>
                      <div className="flex items-center justify-between w-full">
                        <span>{model.name}</span>
                        <Badge className={`ml-2 ${model.badgeColor}`}>
                          {model.badge}
                        </Badge>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            {currentModel && (
              <Badge variant="outline" className="text-xs">
                {currentModel.category}
              </Badge>
            )}
          </div>

          {/* Available Tools */}
          <div className="flex flex-wrap gap-2">
            {tools.map((tool) => (
              <Badge
                key={tool.id}
                variant={tool.enabled ? "default" : "secondary"}
                className={`text-xs ${
                  tool.enabled 
                    ? "bg-green-500/20 text-green-400" 
                    : "bg-gray-500/20 text-gray-400"
                }`}
              >
                {tool.icon}
                <span className="ml-1">{tool.name}</span>
              </Badge>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Chat Messages */}
      <Card className="glass-effect">
        <CardContent className="p-0">
          <div className="h-96 overflow-y-auto p-4 space-y-4">
            {messages.map((message) => (
              <div
                key={message.id}
                className={`flex items-start gap-3 ${
                  message.role === 'user' ? 'flex-row-reverse' : ''
                }`}
              >
                <Avatar className="w-8 h-8 flex-shrink-0">
                  <AvatarFallback className={
                    message.role === 'user' 
                      ? 'bg-primary text-primary-foreground' 
                      : 'bg-purple-500 text-white'
                  }>
                    {message.role === 'user' ? (
                      <User className="w-4 h-4" />
                    ) : (
                      <Bot className="w-4 h-4" />
                    )}
                  </AvatarFallback>
                </Avatar>

                <div className={`flex-1 max-w-[80%] ${
                  message.role === 'user' ? 'text-right' : ''
                }`}>
                  <div className={`p-3 rounded-lg ${
                    message.role === 'user'
                      ? 'bg-primary text-primary-foreground ml-auto'
                      : 'bg-muted'
                  }`}>
                    <p className="text-sm whitespace-pre-wrap">{message.content}</p>
                  </div>

                  <div className={`flex items-center gap-2 mt-1 text-xs text-muted-foreground ${
                    message.role === 'user' ? 'justify-end' : ''
                  }`}>
                    <span>{message.timestamp.toLocaleTimeString()}</span>
                    {message.model && (
                      <span>â€¢ {models.find(m => m.id === message.model)?.name}</span>
                    )}
                    <Button
                      variant="ghost"
                      size="sm"
                      className="h-auto p-1"
                      onClick={() => copyMessage(message.content)}
                    >
                      <Copy className="w-3 h-3" />
                    </Button>
                  </div>
                </div>
              </div>
            ))}

            {/* Typing Indicator */}
            {isTyping && (
              <div className="flex items-start gap-3">
                <Avatar className="w-8 h-8">
                  <AvatarFallback className="bg-purple-500 text-white">
                    <Bot className="w-4 h-4" />
                  </AvatarFallback>
                </Avatar>
                <div className="bg-muted p-3 rounded-lg">
                  <div className="flex items-center gap-1">
                    <div className="flex space-x-1">
                      <div className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce"></div>
                      <div className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                      <div className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                    </div>
                    <span className="text-xs text-muted-foreground ml-2">AI is thinking...</span>
                  </div>
                </div>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>
        </CardContent>
      </Card>

      {/* Message Input */}
      <Card className="glass-effect">
        <CardContent className="p-4">
          <div className="flex items-end gap-2">
            <div className="flex-1">
              <Input
                placeholder="Type your message... (Press Enter to send, Shift+Enter for new line)"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                disabled={chatMutation.isPending}
                className="min-h-[40px] resize-none"
              />
            </div>
            <Button
              onClick={handleSendMessage}
              disabled={chatMutation.isPending || !inputMessage.trim()}
              className="btn-depth"
            >
              {chatMutation.isPending ? (
                <Loader2 className="w-4 h-4 animate-spin" />
              ) : (
                <Send className="w-4 h-4" />
              )}
            </Button>
          </div>

          <div className="flex items-center justify-between mt-2 text-xs text-muted-foreground">
            <div className="flex items-center gap-4">
              <span>Model: {currentModel?.name}</span>
              {websocket && (
                <Badge variant="secondary" className="bg-green-500/20 text-green-400">
                  Connected
                </Badge>
              )}
            </div>
            <span>
              {user?.generationsUsed}/{user?.generationsLimit === -1 ? 'âˆž' : user?.generationsLimit} used
            </span>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/ai/image-generator.content:
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { 
  Sparkles, 
  Wand2, 
  Download, 
  Loader2,
  Settings,
  Image as ImageIcon,
  Palette
} from "lucide-react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";

interface ImageGeneratorProps {
  selectedModel: string;
  onModelChange: (model: string) => void;
  models: Array<{
    id: string;
    name: string;
    provider: string;
    category: string;
    description: string;
    badge: string;
    badgeColor: string;
  }>;
}

export default function ImageGenerator({ selectedModel, onModelChange, models }: ImageGeneratorProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [prompt, setPrompt] = useState("");
  const [enhancePrompt, setEnhancePrompt] = useState(true);
  const [size, setSize] = useState("1024x1024");
  const [quality, setQuality] = useState("standard");
  const [style, setStyle] = useState("vivid");
  const [numberOfImages, setNumberOfImages] = useState([1]);
  const [generatedImages, setGeneratedImages] = useState<any[]>([]);

  const generateImageMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/ai/image/generate", data);
      return response.json();
    },
    onSuccess: (data) => {
      setGeneratedImages(prev => [data, ...prev]);
      toast({
        title: "Image generated successfully!",
        description: data.enhancedPrompt 
          ? "Prompt was enhanced for better results" 
          : "Image created with your prompt",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/generations"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      toast({
        title: "Generation failed",
        description: error.message || "Failed to generate image",
        variant: "destructive",
      });
    },
  });

  const handleGenerate = () => {
    if (!prompt.trim()) {
      toast({
        title: "Prompt required",
        description: "Please enter a description for your image",
        variant: "destructive",
      });
      return;
    }

    if (user?.subscriptionTier === 'free' && user.generationsUsed >= user.generationsLimit) {
      toast({
        title: "Generation limit reached",
        description: "Upgrade to Premium for unlimited generations",
        variant: "destructive",
      });
      return;
    }

    generateImageMutation.mutate({
      model: selectedModel,
      prompt: prompt.trim(),
      enhance: enhancePrompt,
      n: numberOfImages[0],
      size,
      quality,
      style,
    });
  };

  const downloadImage = (imageUrl: string, filename: string) => {
    const link = document.createElement('a');
    link.href = imageUrl;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const sizes = [
    { value: "1024x1024", label: "1024Ã—1024 (Square)" },
    { value: "1792x1024", label: "1792Ã—1024 (Landscape)" },
    { value: "1024x1792", label: "1024Ã—1792 (Portrait)" },
  ];

  const qualities = [
    { value: "standard", label: "Standard Quality" },
    { value: "hd", label: "HD Quality" },
  ];

  const styles = [
    { value: "vivid", label: "Vivid (Dramatic)" },
    { value: "natural", label: "Natural (Realistic)" },
  ];

  return (
    <div className="space-y-6">
      {/* Generation Form */}
      <Card className="glass-effect">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Sparkles className="w-5 h-5" />
            Generate Image
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Model Selection */}
          <div className="space-y-2">
            <Label>AI Model</Label>
            <Select value={selectedModel} onValueChange={onModelChange}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {models.map((model) => (
                  <SelectItem key={model.id} value={model.id}>
                    <div className="flex items-center justify-between w-full">
                      <span>{model.name}</span>
                      <Badge className={`ml-2 ${model.badgeColor}`}>
                        {model.badge}
                      </Badge>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Prompt Input */}
          <div className="space-y-2">
            <Label>Prompt</Label>
            <Textarea
              placeholder="Describe the image you want to create..."
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              rows={4}
              className="resize-none"
            />
          </div>

          {/* Prompt Enhancement */}
          <div className="flex items-center justify-between">
            <div className="space-y-1">
              <Label>AI Prompt Enhancement</Label>
              <p className="text-sm text-muted-foreground">
                Automatically improve your prompt for better results
              </p>
            </div>
            <Switch
              checked={enhancePrompt}
              onCheckedChange={setEnhancePrompt}
            />
          </div>

          {/* Advanced Settings */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {/* Image Size */}
            <div className="space-y-2">
              <Label>Image Size</Label>
              <Select value={size} onValueChange={setSize}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {sizes.map((sizeOption) => (
                    <SelectItem key={sizeOption.value} value={sizeOption.value}>
                      {sizeOption.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Quality */}
            <div className="space-y-2">
              <Label>Quality</Label>
              <Select value={quality} onValueChange={setQuality}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {qualities.map((qualityOption) => (
                    <SelectItem key={qualityOption.value} value={qualityOption.value}>
                      {qualityOption.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Style */}
            <div className="space-y-2">
              <Label>Style</Label>
              <Select value={style} onValueChange={setStyle}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {styles.map((styleOption) => (
                    <SelectItem key={styleOption.value} value={styleOption.value}>
                      {styleOption.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Number of Images */}
            <div className="space-y-2">
              <Label>Number of Images: {numberOfImages[0]}</Label>
              <Slider
                value={numberOfImages}
                onValueChange={setNumberOfImages}
                max={4}
                min={1}
                step={1}
                className="w-full"
              />
            </div>
          </div>

          {/* Generate Button */}
          <Button
            onClick={handleGenerate}
            disabled={generateImageMutation.isPending || !prompt.trim()}
            className="w-full btn-depth"
            size="lg"
          >
            {generateImageMutation.isPending ? (
              <>
                <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                Generating...
              </>
            ) : (
              <>
                <Wand2 className="w-5 h-5 mr-2" />
                Generate Image
              </>
            )}
          </Button>
        </CardContent>
      </Card>

      {/* Generated Images */}
      {generatedImages.length > 0 && (
        <Card className="glass-effect">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <ImageIcon className="w-5 h-5" />
              Generated Images
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {generatedImages.map((imageData, index) => (
                <div key={index} className="space-y-4">
                  {imageData.result?.data?.map((image: any, imgIndex: number) => (
                    <div key={imgIndex} className="relative group">
                      <div className="aspect-square bg-muted rounded-lg overflow-hidden">
                        {image.url ? (
                          <img
                            src={image.url}
                            alt={`Generated image ${imgIndex + 1}`}
                            className="w-full h-full object-cover"
                          />
                        ) : (
                          <div className="w-full h-full flex items-center justify-center">
                            <ImageIcon className="w-12 h-12 text-muted-foreground" />
                          </div>
                        )}
                      </div>

                      {/* Image Actions */}
                      <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center gap-2">
                        <Button
                          size="sm"
                          variant="secondary"
                          onClick={() => downloadImage(image.url, `generated-${Date.now()}.png`)}
                        >
                          <Download className="w-4 h-4" />
                        </Button>
                      </div>

                      {/* Enhanced Prompt Display */}
                      {imageData.enhancedPrompt && (
                        <div className="mt-2 p-3 bg-muted rounded-lg">
                          <p className="text-sm font-medium mb-1">Enhanced Prompt:</p>
                          <p className="text-sm text-muted-foreground">
                            {imageData.enhancedPrompt}
                          </p>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Empty State */}
      {generatedImages.length === 0 && (
        <Card className="glass-effect">
          <CardContent className="py-12 text-center">
            <Palette className="w-16 h-16 mx-auto mb-4 text-muted-foreground" />
            <h3 className="text-lg font-semibold mb-2">Ready to Create</h3>
            <p className="text-muted-foreground">
              Enter a prompt above and generate your first AI image
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/ai/video-generator.content:
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  Video, 
  Play, 
  Download, 
  Loader2,
  Clock,
  Film
} from "lucide-react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";

interface VideoGeneratorProps {
  selectedModel: string;
  onModelChange: (model: string) => void;
  models: Array<{
    id: string;
    name: string;
    provider: string;
    category: string;
    description: string;
    badge: string;
    badgeColor: string;
    maxDuration: string;
    resolution: string;
  }>;
}

export default function VideoGenerator({ selectedModel, onModelChange, models }: VideoGeneratorProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [prompt, setPrompt] = useState("");
  const [generatedVideos, setGeneratedVideos] = useState<any[]>([]);

  const generateVideoMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/ai/video/generate", data);
      return response.json();
    },
    onSuccess: (data) => {
      setGeneratedVideos(prev => [data, ...prev]);
      toast({
        title: "Video generation started!",
        description: "Your video is being processed. This may take a few minutes.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/generations"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      toast({
        title: "Generation failed",
        description: error.message || "Failed to generate video",
        variant: "destructive",
      });
    },
  });

  const handleGenerate = () => {
    if (!prompt.trim()) {
      toast({
        title: "Prompt required",
        description: "Please enter a description for your video",
        variant: "destructive",
      });
      return;
    }

    if (user?.subscriptionTier === 'free' && user.generationsUsed >= user.generationsLimit) {
      toast({
        title: "Generation limit reached",
        description: "Upgrade to Premium for unlimited generations",
        variant: "destructive",
      });
      return;
    }

    generateVideoMutation.mutate({
      model: selectedModel,
      prompt: prompt.trim(),
    });
  };

  const downloadVideo = (videoUrl: string, filename: string) => {
    const link = document.createElement('a');
    link.href = videoUrl;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const currentModel = models.find(m => m.id === selectedModel);

  return (
    <div className="space-y-6">
      {/* Generation Form */}
      <Card className="glass-effect">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Video className="w-5 h-5" />
            Generate Video
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Model Selection */}
          <div className="space-y-2">
            <Label>Video Model</Label>
            <Select value={selectedModel} onValueChange={onModelChange}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {models.map((model) => (
                  <SelectItem key={model.id} value={model.id}>
                    <div className="flex items-center justify-between w-full">
                      <span>{model.name}</span>
                      <Badge className={`ml-2 ${model.badgeColor}`}>
                        {model.badge}
                      </Badge>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            {/* Model Info */}
            {currentModel && (
              <div className="p-3 bg-muted rounded-lg text-sm space-y-1">
                <p><strong>Resolution:</strong> {currentModel.resolution}</p>
                <p><strong>Max Duration:</strong> {currentModel.maxDuration}</p>
                <p className="text-muted-foreground">{currentModel.description}</p>
              </div>
            )}
          </div>

          {/* Prompt Input */}
          <div className="space-y-2">
            <Label>Video Description</Label>
            <Textarea
              placeholder="Describe the video you want to create. Include details about motion, scene, lighting, and style..."
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              rows={6}
              className="resize-none"
            />
            <p className="text-sm text-muted-foreground">
              ðŸ’¡ Tip: Describe movement and actions clearly. For example: "A golden retriever running through a sunlit meadow, slow motion, cinematic lighting"
            </p>
          </div>

          {/* Generate Button */}
          <Button
            onClick={handleGenerate}
            disabled={generateVideoMutation.isPending || !prompt.trim()}
            className="w-full btn-depth"
            size="lg"
          >
            {generateVideoMutation.isPending ? (
              <>
                <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                Generating Video...
              </>
            ) : (
              <>
                <Film className="w-5 h-5 mr-2" />
                Generate Video
              </>
            )}
          </Button>

          {/* Processing Info */}
          <div className="flex items-center gap-2 text-sm text-muted-foreground">
            <Clock className="w-4 h-4" />
            <span>Video generation typically takes 2-5 minutes</span>
          </div>
        </CardContent>
      </Card>

      {/* Generated Videos */}
      {generatedVideos.length > 0 && (
        <Card className="glass-effect">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Video className="w-5 h-5" />
              Generated Videos
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {generatedVideos.map((videoData, index) => (
                <div key={index} className="space-y-4">
                  <div className="relative group">
                    <div className="aspect-video bg-muted rounded-lg overflow-hidden">
                      {videoData.result?.url ? (
                        <video
                          controls
                          className="w-full h-full object-cover"
                          poster={videoData.result?.thumbnail}
                        >
                          <source src={videoData.result.url} type="video/mp4" />
                          Your browser does not support the video tag.
                        </video>
                      ) : (
                        <div className="w-full h-full flex items-center justify-center">
                          <div className="text-center">
                            {generateVideoMutation.isPending ? (
                              <>
                                <Loader2 className="w-12 h-12 mx-auto mb-2 animate-spin text-primary" />
                                <p className="text-sm text-muted-foreground">Processing video...</p>
                              </>
                            ) : (
                              <>
                                <Video className="w-12 h-12 mx-auto mb-2 text-muted-foreground" />
                                <p className="text-sm text-muted-foreground">Video will appear here</p>
                              </>
                            )}
                          </div>
                        </div>
                      )}
                    </div>

                    {/* Video Actions */}
                    {videoData.result?.url && (
                      <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center gap-2">
                        <Button
                          size="sm"
                          variant="secondary"
                          onClick={() => downloadVideo(videoData.result.url, `generated-video-${Date.now()}.mp4`)}
                        >
                          <Download className="w-4 h-4" />
                        </Button>
                      </div>
                    )}
                  </div>

                  {/* Video Info */}
                  <div className="p-3 bg-muted rounded-lg">
                    <p className="text-sm font-medium mb-1">Prompt:</p>
                    <p className="text-sm text-muted-foreground">
                      {videoData.prompt || prompt}
                    </p>
                    <div className="flex items-center gap-4 mt-2 text-xs text-muted-foreground">
                      <span>Model: {selectedModel.split('/')[1]}</span>
                      <span>Status: {videoData.status || 'Processing'}</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Empty State */}
      {generatedVideos.length === 0 && (
        <Card className="glass-effect">
          <CardContent className="py-12 text-center">
            <Film className="w-16 h-16 mx-auto mb-4 text-muted-foreground" />
            <h3 className="text-lg font-semibold mb-2">Ready to Create Videos</h3>
            <p className="text-muted-foreground mb-4">
              Enter a detailed description above and generate your first AI video
            </p>
            <div className="text-sm text-muted-foreground space-y-1">
              <p>â€¢ Describe movement and actions clearly</p>
              <p>â€¢ Include lighting and scene details</p>
              <p>â€¢ Specify camera angles if needed</p>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

