import { useState } from "react";
import { Dialog, DialogContent, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useSignIn, useSignUp, useResetPassword, useGoogleSignIn } from "@/hooks/useAuth";
import { Loader2, ArrowLeft, Mail, Lock, User, Eye, EyeOff } from "lucide-react";

interface AuthModalProps {
  isOpen: boolean;
  onClose: () => void;
  defaultTab?: "signin" | "signup";
}

export function AuthModal({ isOpen, onClose, defaultTab = "signin" }: AuthModalProps) {
  const [activeView, setActiveView] = useState<"signin" | "signup" | "forgot">(defaultTab);

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[500px] border-0 bg-transparent p-0 shadow-none" aria-describedby="auth-description">
        <DialogTitle className="sr-only">Authentication</DialogTitle>
        <div id="auth-description" className="sr-only">Sign in or create an account for LoveAIHub</div>

        {/* Background glow effect */}
        <div className="absolute inset-0 rounded-3xl bg-gradient-to-r from-purple-500/20 via-blue-500/20 to-purple-600/20 blur-xl scale-110"></div>

        {/* Main container with purple gradient border */}
        <div className="relative">
          {/* Gradient border */}
          <div className="absolute inset-0 rounded-3xl bg-gradient-to-r from-purple-500 via-blue-500 to-purple-600 p-[2px]">
            <div className="h-full w-full rounded-3xl bg-slate-900"></div>
          </div>

          {/* Content */}
          <div className="relative rounded-3xl bg-slate-900 p-8">
            {activeView === "signin" && (
              <SignInForm 
                onForgotPassword={() => setActiveView("forgot")} 
                onSignUpClick={() => setActiveView("signup")}
                onSuccess={onClose} 
              />
            )}

            {activeView === "signup" && (
              <SignUpForm 
                onSignInClick={() => setActiveView("signin")}
                onSuccess={onClose} 
              />
            )}

            {activeView === "forgot" && (
              <ForgotPasswordForm onBack={() => setActiveView("signin")} />
            )}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

function SignInForm({ onForgotPassword, onSignUpClick, onSuccess }: { 
  onForgotPassword: () => void; 
  onSignUpClick: () => void;
  onSuccess: () => void; 
}) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const signIn = useSignIn();
  const googleSignIn = useGoogleSignIn();

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    signIn.mutate({ email, password }, {
      onSuccess: () => onSuccess(),
    });
  };

  return (
    <div className="w-full space-y-6">
      {/* Header */}
      <div className="text-center space-y-3">
        <h1 className="text-3xl font-bold text-white">Welcome Back</h1>
