    if (password !== confirmPassword) {
      alert("Passwords don't match");
      return;
    }
    const [firstName, ...lastNameParts] = fullName.split(" ");
    const lastName = lastNameParts.join(" ");
    signUp.mutate({ email, password, firstName, lastName }, {
