import { useState, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Progress } from "@/components/ui/progress";
import { Separator } from "@/components/ui/separator";
import { 
  Menu, 
  Sparkles, 
  Home,
  Image, 
  Video, 
  MessageSquare, 
  Mic, 
  Edit, 
  Code,
  BookOpen,
  Settings,
  Crown,
  LogOut,
  ChevronLeft,
  ChevronRight,
  User,
  Star,
  Clock,
  BarChart3,
  Zap,
  TrendingUp
} from "lucide-react";

interface DashboardLayoutProps {
  children: React.ReactNode;
}

export default function DashboardLayout({ children }: DashboardLayoutProps) {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [hoveredItem, setHoveredItem] = useState<string | null>(null);
  const { user } = useAuth();
  const [location] = useLocation();

  const navigationItems = [
    {
      title: "Dashboard",
      href: "/",
      icon: Home,
      description: "Overview and analytics",
      badge: null,
      gradient: "from-blue-500 to-cyan-500"
    },
    {
      title: "Image Studio",
      href: "/image-generation",
      icon: Image,
      description: "AI image generation",
      badge: "Hot",
      gradient: "from-purple-500 to-pink-500"
    },
    {
      title: "Video Lab",
      href: "/video-generation",
      icon: Video,
      description: "AI video creation",
      badge: "New",
      gradient: "from-green-500 to-emerald-500"
    },
    {
      title: "AI Chat",
      href: "/ai-chat",
      icon: MessageSquare,
      description: "Conversational AI",
      badge: null,
      gradient: "from-orange-500 to-red-500"
    },
    {
      title: "Audio Tools",
      href: "/audio-speech",
      icon: Mic,
      description: "Speech synthesis",
      badge: null,
      gradient: "from-indigo-500 to-purple-500"
    },
    {
      title: "Image Editor",
      href: "/image-editing",
      icon: Edit,
      description: "AI-powered editing",
      badge: null,
      gradient: "from-teal-500 to-cyan-500"
    },
    {
      title: "API Docs",
      href: "/api-docs",
      icon: Code,
      description: "Developer resources",
      badge: null,
      gradient: "from-slate-500 to-gray-500"
    },
    {
      title: "Blog",
      href: "/blog",
      icon: BookOpen,
      description: "Latest updates",
      badge: null,
      gradient: "from-amber-500 to-orange-500"
    }
  ];

  const handleLogout = () => {
    window.location.href = "/api/logout";
  };

  const isActive = (href: string) => {
    if (href === "/") return location === "/";
    return location.startsWith(href);
  };

  const SidebarContent = ({ mobile = false }: { mobile?: boolean }) => (
    <div className="flex flex-col h-full glass-card border-r border-border/20">
      {/* Header with enhanced styling */}
      <div className="relative p-6 border-b border-border/20 bg-gradient-to-r from-primary/5 to-purple-500/5">
        <div className="flex items-center justify-between">
          <Link href="/" className="flex items-center space-x-3 group">
            <div className="relative">
              <div className="w-10 h-10 rounded-xl bg-gradient-to-r from-primary to-purple-500 flex items-center justify-center shadow-lg transform group-hover:scale-105 transition-transform duration-200">
